mapper.select:
select * from (select rownum rnum , a.*, count(*) over() as cnt from ( select * from tb_free_board where study_no = #{studyNum}
and email like '%'||#{keyword}||'%'
and title like '%'||#{keyword}||'%'
order by notice asc,
reg_date desc,
num desc) a) where rnum between #{begin} and #{end}

mapper.update:
update tb_free_board
   set title = #{title},
      content = #{content}
      where num = #{num}
