mapper.insert:
insert into tb_comment (
num, comment_group_code, email, content, comment_group
) values (
seq_comment_num.nextval, #{commentGroupCode}, #{email}, #{content}, #{commentGroup}
)

insert into tb_comment (
num, comment_group_code, email, content ,parent, comment_group
) values (
seq_comment_num.nextval, #{commentGroupCode}, #{email}, #{content}, #{parent}, #{commentGroup}
)

mapper.update:
update tb_comment
  set content = #{content}
 where num = #{num}
