mapper.select:
select *
from (select rownum rnum,
a.*,
count(*) over() as cnt
from tb_question_board a)
where rnum between #{begin} and #{end}
and email = #{email}
order by reg_date desc

select * from (select rownum rnum, a.*, count(*) over() as cnt from(
select * from tb_question_board where num = num
and title like '%'||#{keyword}||'%'
and email like '%'||#{keyword}||'%'
order by condition desc
) a) where rnum between #{begin} and #{end}

mapper.insert:
insert into tb_comment (
	email, content, num, comment_group_code
) values (
	#{email}, #{content}, seq_comment_num.nextVal, #{commentGroupCode}
)
