mapper.select:
select s.*,nvl(p.c,0) count ,nvl(o.open,0) open ,nvl(j.join,0) join from tb_user s,
(SELECT REPORT_TARGET,count(*) c from tb_report where type = 'user' group by REPORT_TARGET) p, (select EMAIL,count(*) open from TB_STUDY group by EMAIL) o, (SELECT r.EMAIL,count(*) join FROM TB_PARTICIPANT r where CONDITION = 2 group by EMAIL) j
where s.EMAIL = p.REPORT_TARGET(+) and s.EMAIL = o.EMAIL(+) and s.EMAIL = j.EMAIL(+) and s.EMAIL = #{email}
