mapper.delete:
delete
from tb_participant
where email = #{email} and study_num = #{studyNum}

mapper.insert:
insert into 
tb_review_user (score, num, email, email_recom
) values (#{score}, #{num}, #{email}, #{emailRecom})

mapper.select:
select count(*)
  from tb_review_user
 where num = #{num} and email = #{email} and email_recom = #{emailRecom}

		select email,num, study_no, content, to_char(start_date,'yyyymmdd') as start_date, to_char(end_date,'yyyymmdd') as end_date, type, font_color,bar_color  ,title
  		from TB_CALENER
 		where num = #{num}

select count(*)
  from tb_participant
 where study_num = #{studyNum}
   and email = #{email}
