mapper.select:
select * from (select rownum as rnum, a.*, count(*) over() as count from (select str.*, nvl(r.avr, 0) avr, nvl(man, 1) pcnt from tb_study_recruitment str, (select study_num, avg(score) avr from tb_review_study group by study_num) r, (select study_num, count(*) man from tb_participant where condition = 2 group by study_num) p where str.study_num = r.study_num(+) and str.study_num = p.study_num(+)
and str.email like '%' || #{keyword} || '%'
and str.title like '%' || #{keyword} || '%'
and str.content like '%' || #{keyword} || '%'
and category_code = #{categoryCode}
order by str.see_cnt desc
order by str.reg_date desc
order by avr desc
) a) where rnum between #{begin} and #{end}

mapper.update:
		update tb_study_recruitment
  		   set title = #{title},
   	       content = #{content}
        where num = #{num}

update tb_study_recruitment
   set see_cnt = see_cnt + 1
 where num = #{num}
